//城市添加到导航条
function addCtiy(){
		for(var i=0;i<en.length;i++){
			var ying=en[i];
			$(".navbar-nav .dl-horizontal").append($("<div><dt>"+ying+"</dt></div>"));
		}
		for(var ch=0;ch<city.length;ch++){
			var shou=makePy(city[ch]);
			$(".navbar-nav .dl-horizontal dt").each(function(){
				if(shou==$(this).text()){
					$(this).after($("<dd>"+city[ch]+"</dd>"))
				}
			})
		}
		$(".navbar-nav .dl-horizontal div").each(function(){
				if($(this).children("dd").length==0){
					$(this).remove();
				}
		})
		
}
addCtiy();
