    //判断用户是否登录显示不同信息表
    function userKuangQieHuan(){
    	//alert("进去");
    	var userID=$("#userName").attr("userid");
    	if(userID!="-1"&&userID.length>0){
    		$("#xiuGaiinputText").attr("disabled","disabled");
       		$("#xiuGaiinputPassword").attr("disabled","disabled");
       		$("#xiuGaibody [name='xiuGaisex']").attr("disabled","disabled");
       		$("#xiuGaiChexkbox").removeAttr("disabled");
       		$.post("./selectUserName","userID="+userID,function(data){
    			//console.info(data);
    			$("#xiuGaiinputText").val(data.username);
    			$("#xiuGaiinputPassword").val(data.password);
    			$("#xiuGaibody [name='xiuGaisex']").each(function(){
    				if($(this).val()=="女"){
    					$(this).attr("checked","checked");
    				}
    			})
    			if(data.title_img!=null){
    				var lujing=$("#titleimg").attr("lujing");
    				//$("#titleimg").attr("src","http://localhost:8080"+lujing+data.title_img);
    				$(".img-rounded").attr("src","http://localhost:8080"+lujing+data.title_img);
    			}
    		},"JSON")
    		 $("#xiuGai").modal("toggle");
    	}else{
    		 $("#inputText").val("");
    		 $("#inputPassword").val("");
    		 $("#zhuCe").modal("toggle");
    	}
    }
